mapper.select:
SELECT COL_CD, COL_NM, COL_DES, COL_TEL, COL_ADDR, COL_IMG_URL
FROM COLLEGE
WHERE COL_CD = #{colCd}

mapper.update:
UPDATE COLLEGE
SET   
      COL_NM = #{colNm}
    , COL_DES = #{colDes}
    , COL_TEL = #{colTel}
    , COL_ADDR = #{colAddr}
    , COL_IMG_URL = #{colImgUrl} 
WHERE COL_CD = #{colCd}
