mapper.insert:
INSERT INTO COUNSEL (
  CNSL_CD
, STU_NO
, CNSL_CATE
, CNSL_TTL
, CNSL_CON
, CNSL_REG
, PRO_NO
)
VALUES(

SUBSTR(TO_NUMBER(TO_CHAR(SYSDATE,'YYYYMMDD')),1,6)||'000'||COUNSEL_SEQ.NEXTVAL

, #{stuNo}
, #{cnslCate}
, #{cnslTtl}
, #{cnslCon}
, SYSDATE
, #{proNo}
)

mapper.update:
UPDATE COUNSEL 
SET 
 CNSL_RPL = #{cnslRpl}
, PRO_NO = #{proNo}
WHERE CNSL_CD = #{cnslCd}
