select.if:
WITH T AS ( SELECT NVL(COUNT(S.STU_NO),0) RECRUITMENT_RATE, C.COL_NM, C.COL_CD FROM COLLEGE C, DEPARTMENT D, STUDENT S WHERE D.DEP_CD = S.DEP_CD AND C.COL_CD = D.COL_CD (+)
AND SUBSTR(S.STU_NO,1,4) = #{yr}
GROUP BY C.COL_NM, C.COL_CD ) SELECT NVL(T.RECRUITMENT_RATE,0) RECRUITMENT_RATE, TC.COL_NM, TC.COL_CD, ( SELECT NVL(SUM(DE.DEP_CAP),0) FROM DEPARTMENT DE WHERE DE.COL_CD = TC.COL_CD ) CAPACITY FROM T, COLLEGE TC WHERE T.COL_CD (+) = TC.COL_CD ORDER BY TC.COL_NM

WITH T AS ( SELECT NVL(COUNT(S.STU_NO),0) RECRUITMENT_RATE, D.DEP_NM, D.DEP_CD FROM DEPARTMENT D, STUDENT S WHERE D.DEP_CD(+) = S.DEP_CD
AND SUBSTR(S.STU_NO,1,4) = #{yr}
GROUP BY D.DEP_NM, D.DEP_CD ) SELECT NVL(T.RECRUITMENT_RATE,0) RECRUITMENT_RATE, TD.DEP_NM, TD.DEP_CD, TD.DEP_CAP CAPACITY FROM T, DEPARTMENT TD WHERE T.DEP_CD (+) = TD.DEP_CD AND TD.COL_CD = #{colCd} ORDER BY TD.DEP_NM

SELECT C.COL_NM
     , (SELECT NVL(COUNT(S1.STU_NO),0) FROM STUDENT S1, DEPARTMENT D1 WHERE S1.DEP_CD = D1.DEP_CD AND D1.COL_CD = C.COL_CD 

		     		AND SUBSTR(STU_NO,1,4) = #{yr}

     	) ALL_STU
     , (SELECT NVL(COUNT(S2.STU_NO),0) FROM STUDENT S2, DEPARTMENT D2 WHERE S2.DEP_CD = D2.DEP_CD AND D2.COL_CD = C.COL_CD AND STU_RGB = 'RCD006' 

		     		AND SUBSTR(STU_NO,1,4) = #{yr}

		) IN_SCHOOL
     , (SELECT NVL(COUNT(S3.STU_NO),0) FROM STUDENT S3, DEPARTMENT D3 WHERE S3.DEP_CD = D3.DEP_CD AND D3.COL_CD = C.COL_CD AND STU_RGB IN( 'RCD001', 'RCD002') 

		     		AND SUBSTR(STU_NO,1,4) = #{yr}

    	) LEAVE_OF_ABSENCE
     , (SELECT NVL(COUNT(S4.STU_NO),0) FROM STUDENT S4, DEPARTMENT D4 WHERE S4.DEP_CD = D4.DEP_CD AND D4.COL_CD = C.COL_CD AND STU_RGB = 'RCD004' 

	     			AND SUBSTR(STU_NO,1,4) = #{yr}

     	) EXPELLED
     , (SELECT NVL(COUNT(S5.STU_NO),0) FROM STUDENT S5, DEPARTMENT D5 WHERE S5.DEP_CD = D5.DEP_CD AND D5.COL_CD = C.COL_CD AND STU_RGB = 'RCD005' 

	     			AND SUBSTR(STU_NO,1,4) = #{yr}

     	) GRADUATE
FROM COLLEGE C

		SELECT D.DEP_NM
		     , (SELECT NVL(COUNT(S1.STU_NO),0) FROM STUDENT S1, DEPARTMENT D1 WHERE S1.DEP_CD = D1.DEP_CD AND D1.DEP_CD = D.DEP_CD AND D1.COL_CD = #{colCd}

			     			AND SUBSTR(STU_NO,1,4) = #{yr}

	    		) ALL_STU
		     , (SELECT NVL(COUNT(S2.STU_NO),0) FROM STUDENT S2, DEPARTMENT D2 WHERE S2.DEP_CD = D2.DEP_CD AND D2.DEP_CD = D.DEP_CD AND STU_RGB = 'RCD006' AND D2.COL_CD = #{colCd} 

			     			AND SUBSTR(STU_NO,1,4) = #{yr}

	    		) IN_SCHOOL
		     , (SELECT NVL(COUNT(S3.STU_NO),0) FROM STUDENT S3, DEPARTMENT D3 WHERE S3.DEP_CD = D3.DEP_CD AND D3.DEP_CD = D.DEP_CD AND STU_RGB IN( 'RCD001', 'RCD002') AND D3.COL_CD = #{colCd}  

			     			AND SUBSTR(STU_NO,1,4) = #{yr}

	    		) LEAVE_OF_ABSENCE
		     , (SELECT NVL(COUNT(S4.STU_NO),0) FROM STUDENT S4, DEPARTMENT D4 WHERE S4.DEP_CD = D4.DEP_CD AND D4.DEP_CD = D.DEP_CD AND STU_RGB = 'RCD004' AND D4.COL_CD = #{colCd}

			     			AND SUBSTR(STU_NO,1,4) = #{yr}

	    		) EXPELLED
		     , (SELECT NVL(COUNT(S5.STU_NO),0) FROM STUDENT S5, DEPARTMENT D5 WHERE S5.DEP_CD = D5.DEP_CD AND D5.DEP_CD = D.DEP_CD AND STU_RGB = 'RCD005' AND D5.COL_CD = #{colCd}

			     			AND SUBSTR(STU_NO,1,4) = #{yr}

    			) GRADUATE
		FROM DEPARTMENT D
