mapper.select:
SELECT A.MEM_NO, A.MEM_ML, A.MEM_PASS, AUTH.AUTH
FROM   MEMBER A LEFT OUTER JOIN MEMBER_AUTH AUTH ON(A.MEM_NO = AUTH.MEM_NO)
WHERE  A.MEM_NO = #{memNo}

SELECT STU_NO, DEP_CD, STU_YR, STU_SEM, STU_NM, STU_NME, STU_TEL, STU_ZIP, STU_ADDR1, STU_ADDR2, STU_REG, STU_BANK_CD, STU_DEPO, STU_ACT, STU_PIC
FROM STUDENT
WHERE STU_NO = #{memNo}

SELECT EMP_NO, EMP_NM, EMP_NME, EMP_TEL, EMP_TEL2, EMP_ZIP, EMP_ADDR1, EMP_ADDR2, EMP_REG, EMP_BANK_CD, EMP_DEPO, EMP_ACT, EMP_PIC, EMP_JOIN, EMP_RET
FROM EMPLOYEE
WHERE EMP_NO = #{memNo}

SELECT MGR_NO, MGR_DIV, MGR_POS
FROM MANAGER
WHERE MGR_NO = #{memNo}

SELECT PRO_NO, DEP_CD, PRO_POS
FROM PROFESSOR
WHERE PRO_NO = #{memNo}
