mapper.select:
SELECT A.MEM_NO, A.MEM_TEL, A.MEM_PASS, AUTH.AUTH
FROM   MEMBER A LEFT OUTER JOIN MEMBER_AUTH AUTH ON(A.MEM_NO = AUTH.MEM_NO)
WHERE  A.MEM_NO = #{memNo}

SELECT EMP_NO, EMP_NM, EMP_NME, EMP_TEL, EMP_TEL2, EMP_ZIP, EMP_ADDR1, EMP_ADDR2, EMP_REG, EMP_BANK_CD, EMP_DEPO, EMP_ACT, EMP_PIC, EMP_JOIN, EMP_RET
FROM EMPLOYEE
WHERE EMP_NO = #{memNo}

SELECT MGR_NO, MGR_DIV, MGR_POS
FROM MANAGER
WHERE MGR_NO = #{memNo}

SELECT PRO_NO, DEP_CD, PRO_POS
FROM PROFESSOR
WHERE PRO_NO = #{memNo}

SELECT MEM_NO 
FROM MEMBER LEFT OUTER JOIN STUDENT ON(MEM_NO = STU_NO) 
WHERE STU_NM = #{memNm}
AND STU_BIR = #{memBir}
AND STU_TEL = #{memTel}


UPDATE MEMBER
SET MEM_PASS = #{memPass}
WHERE MEM_TEL = #{memTel}
AND MEM_NO = #{memNo}

mapper.update:
UPDATE STUDENT
SET STU_ZIP = #{stuZip}, STU_ADDR1 = #{stuAddr1}, STU_ADDR2 = #{stuAddr2}, STU_BANK_CD = #{stuBankCd}, STU_ACT = #{stuAct}
WHERE STU_NO = #{stuNo}

UPDATE MEMBER
SET MEM_PASS = #{newPw}
WHERE MEM_PASS = #{oldPw}
AND MEM_NO = #{stuNo}
