mapper.insert:
INSERT INTO RECORD (
		  REC_CD
		, STU_NO
		, RGB_CD
		, REC_YR
		, REC_SEM
		, REC_YN
		, REC_DT
		, REC_PER
		, REC_RSN
		)
VALUES(
		RECORD_SEQ.NEXTVAL
		, #{stuNo}
		, #{rgbCd}
		, TO_NUMBER(TO_CHAR(SYSDATE,'YYYY'))
		, #{recSem}
		, #{recYn}
		, SYSDATE
		, #{recPer}
		, #{recRsn}
		)
