mapper.delete:
DELETE FROM STU_LEC
WHERE STU_NO = #{stuNo}
AND LECA_CD = #{lecaCd}

mapper.update:
UPDATE LECTURE
    SET LEC_HCNT = (SELECT LEC_HCNT FROM LECTURE WHERE LECA_CD = #{lecaCd}) + 1
WHERE LECA_CD = #{lecaCd}


UPDATE LECTURE
    SET LEC_HCNT = (SELECT LEC_HCNT FROM LECTURE WHERE LECA_CD = #{lecaCd}) - 1
WHERE LECA_CD = #{lecaCd}

mapper.select:
SELECT COUNT(*)
FROM LECTURE L
WHERE L.LECA_CD = #{lecaCd}
AND L.LEC_HCNT < (
    SELECT LA.LECA_CAP
    FROM LEC_APPLY LA
    WHERE LA.LECA_CD = #{lecaCd}
)
